INSERT INTO `sys_book` VALUES ('534', '634', 'F260/Y496a', '产业经济学', '产业经济学-高等学校-教材', '本书以主流产业经济学的内容框架为范本，将全部内容划分为三篇：第1篇产业发展，共分六章，包括产业结构、产业关联和产业布局等内容，这是产业经济学中的宏观内容。其中“现代产业体系”一章作为产业结构调整与产业发展最新成果的反映。第2篇产业组织，共分7章，包括理论范式、同质产品的价格与产量竞争、差异化产品的市场竞争、市场进入�', 2017, '于立宏,孔令丞', '978-7-301-28548-0', '', 'F260-43', '', '北京大学出版社', '2019-01-04 17:58:02', '', NULL, '1229567', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('535', '635', 'F062.9/Z3384', '产业经济学:原理及案例', '产业经济学-高等学校-教材', '本书采用从宏观到微观、从理论到应用的逻辑顺序，构造了产业经济学的理论体系框架。全书共十二章。第一章为绪论，主要阐述产业经济学的产生与发展、对象与内容、意义与方法；第二章至第六章是作为产业经济学宏观层次的产业结构理论，主要阐述产业结构的演变规律及其影响因素、产业结构优化的标准和升级方向、产业布局与产业集聚、高技术�', 2017, '赵玉林', '978-7-300-24465-5', '', 'F062.9-43,F260-43', '', '中国人民大学出版社', '2019-01-04 17:58:02', '', NULL, '1219418', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
