INSERT INTO `sys_book` VALUES ('561', '661', 'null', '创新、竞争与平台经济:诺贝尔经济学奖得主论文集', '经济学—文集', '本书作者为荣获2014年诺贝尔经济学奖的图卢兹经济学院(ToulouseSchoolofEconomics)的让•梯若尔教授,在本书中,梯若尔教授对软件开源、平台设计与定价、知识产权、电信、支付平台和与之相关联的反垄断问题都进行了深入透彻的分析。选取主题挑选切合中国目前经济发展所面临的挑战和问题的文章。从研究方法的角度来讲,', 2017, '梯若尔,寇宗来', '978-7-5197-0767-5', '', 'F0-53', '', '法律出版社', '2019-01-04 17:58:03', '', NULL, '1232340', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('562', '662', 'F120.2/Y164', '创新发展的社会主义政治经济学', '中国特色社会主义-社会主义政治经济学-研究', '本书共七章，内容包括：大力发展社会主义生产力、社会主义基本经济制度理论创新发展、社会主义经济本质理论、社会主义市场经济理论、全球化与对外开放等。', 2016, '严法善', '978-7-309-12682-2', '', 'F120.2', '', '复旦大学出版社有限公司', '2019-01-04 17:58:03', '', NULL, '1183807', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('563', '663', 'F260/W199c', '创新集群三十年探索之旅', '产业经济学-研究', '本书作者王缉慈将她的少数代表性论文和演讲稿分别汇集为“研究之旅”篇和“传授之道”篇，并穿插着她在学术研究背后的故事。书中折射了她不断追求和探索以创新驱动区域发展的心路历程，反映了她对我国各类开发园区和专业城镇发展的独特观点，以及对高新技术产业等。', 2016, '王缉慈', '978-7-03-050422-7', '', 'F260', '', '科学出版社', '2019-01-04 17:58:03', '', NULL, '1174714', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
