INSERT INTO `sys_book` VALUES ('591', '691', 'C924.24/Q474', '二孩经济学', '人口经济学-研究-中国', '本书主要介绍了二孩政策推行的原因、现状以及其对各个群体带来的预期效应;讲述了二孩政策对于家庭的影响,通过经济、情感、身体、育儿四个方向的剖析,帮助有意愿生养二胎的夫妻判断自身是否适合生二胎,以及在备孕二胎、抚养两个孩子的过程中应注意的事项和相关技巧;还讲述了二孩政策对于企业的影响,', 2016, '全忠伟', '978-7-308-16049-0', '', 'C924.24', '', '浙江大学出版社', '2019-01-04 17:58:04', '', NULL, '1153617', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
