INSERT INTO `sys_book` VALUES ('594', '694', 'F061.3/G2112', '发展经济学', '发展经济学', '本书从经济解释的视角综合和介绍各种发展理论，并注重对中国发展经验的研究，以促进发展经济学融入经济学研究的主流和更好地发挥这门学科的效能。根据定位，本书首先探讨了发展的目标，介绍了发展范式的演变、增长理论和增长模型及其实证结果。', 2017, '高波', '978-7-305-19079-7', '', 'F061.3', '', '南京大学出版社', '2019-01-04 17:58:04', '', NULL, '1225437', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('595', '695', 'F061.3/X463b', '发展经济学', '发展经济学-高等教育-教材', '本书共分十六章，内容包括：绪论、现代经济增长理论、现代经济发展理论、资本形成、人力资本与经济发展、技术进步与经济发展、可持续发展、国际贸易与经济发展、经济全球化与国际经济新秩序等。', 2017, '徐筑燕', '978-7-03-051058-7', '', 'F061.3-43', '', '科学出版社', '2019-01-04 17:58:04', '', NULL, '1186111', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('596', '696', 'F061.3/L856', '发展经济学', '发展经济学-高等学校-教材', '本书共分为三篇:第一篇介绍经济发展领域所涉及的所有主要问题,涵盖了贫困和不平等性、人口、经济增长、结构变迁等问题。第二篇重点讨论制度及其在经济发展中的作用。第三篇探讨发展经济学家一直致力于研究的核心问题,其中有关产权、腐败和冲突的章节是本书颇具特色的内容。', 2016, '罗兰,金志农', '978-7-300-23379-6', '', 'F061.3-43', '', '中国人民大学出版社', '2019-01-04 17:58:04', '', NULL, '1163730', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
