INSERT INTO `sys_book` VALUES ('604', '704', 'null', '法经济学', '法学-经济学-教材', '本书是法经济学的入门教材。主要内容包括法经济学的概念、发展历史和基本理论，财产法、知识产权法、合同法、侵权法、司法程序、刑法、公司法、管制等部门法的经济分析，以及法律与经济增长的研究进展。', 2017, '魏建', '978-7-300-24945-2', '', 'D90-059', '', '中国人民大学出版社', '2019-01-04 17:58:05', '', NULL, '1239360', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('605', '705', 'D90-056/T196', '法经济学基础理论研究', '法学-经济学-研究', '本书共分为十六章,其主要内容包括:导论;法经济学的发展史;法经济学的法学基础;法经济学的经济学基础;科斯定理;法律与经济增长;财产法的法经济学理论;合同法的法经济学理论等。', 2017, '汤自军', '978-7-5643-5618-7', '', 'D90-056', '', '西南交通大学出版社', '2019-01-04 17:58:05', '', NULL, '1228099', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('606', '706', 'null', '法律经济学', '法律经济学', '本书基于让学生对法律经济学有个初步的把握,着力于学生法律经济学思维方法的培养,按照逻辑顺序将内容划分为“历史篇”“理论篇”与“应用篇”。“历史篇”主要梳理、研究法律经济学的发展史、法律经济学派回顾与展望、中国与法律经济学。“理论篇”主要依据法律经济学经典文献,以及不同学派的理论,', 2017, '熊春泉', '978-7-5620-7840-1', '', 'D90-056', '', '中国政法大学出版社', '2019-01-04 17:58:05', '', NULL, '1248080', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('607', '707', 'D90-056/L624', '法律经济学', '法学-经济学-高等学校-教材', '本书以具体案例、设例及小栏目等方式，通过知识产权法、合同法、公司法、反垄断法、劳动法、社会保障法、环境法等多个领域来比较法学与经济学各自思维的精髓，解读两者之间的异同，力图让读者同时理解两大学科在解决社会问题时所体现出的各自特征。', 2017, '柳川隆,高桥裕,大内伸哉,吴波', '978-7-111-55883-5', '', 'D90-056', '', '机械工业出版社', '2019-01-04 17:58:05', '', NULL, '1197914', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('608', '708', 'D90-056/W198', '法治的经济效应测度研究', '法律经济学-研究', '本书通过构建法治测度体系将一系列抽象的法治概念转化成具体的能够量化的指标体系,进而以此指标体系对中国法治对经济发展的作用进行实证研究。同时,对中国与世界其他国家的法治对经济发展的作用进行国际比较,以此正确认识法治与经济发展理论在中国的适应性和重要性。', 2017, '王红云', '978-7-208-14770-6', '', 'D90-056', '', '上海人民出版社', '2019-01-04 17:58:05', '', NULL, '1241271', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('609', '709', 'F038.2/Y515', '反垄断经济学', '垄断经济学-研究', '本书以反垄断三大支柱为核心,按照现象描述、理论分析、福利权衡、政策法规、司法判定的逻辑顺序安排内容。然后,根据我国反垄断法的特殊性,增加了反行政性垄断的内容,使其成为中国反垄断政策的第四大支柱。在阐述传统理论、主流观点和经典范式的同时,', 2017, '余东华', '978-7-5141-8335-1', '', 'F038.2,F014.9', '', '经济科学出版社', '2019-01-04 17:58:05', '', NULL, '1224896', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
