INSERT INTO `sys_book` VALUES ('611', '711', 'null', '房地产经济学', '房地产经济学-高等学校-教材', '本书共分十一章,第一章至第六章主要是从微观角度介绍房地产经济学的基本理论和方法,第七章至第十一章则从宏观角度介绍与房地产经济及市场相关的重要制度问题。本书在编排结构时,既注重对房地产基本理论和知识的阐释,又注意结合中国房地产行业特性和制度特点进行详解,尽可能兼顾普遍性和特殊性的要求。在具体内容上,', 2017, '董藩', '978-7-302-47870-6', '', 'F293.30', '', '清华大学出版社', '2019-01-04 17:58:05', '', NULL, '1235405', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
