INSERT INTO `sys_book` VALUES ('624', '724', 'F0-52/N146/(1)', '弗兰克•奈特文集.卷一,经济学的真理', '经济学-文集', '本书按照时间顺序收录了弗兰克•奈特的14篇论文,包括《科学方法在经济学中的局限性》《伦理学与经济解释》《竞争的伦理学》《社会成本解释中的一些谬误》《经济心理学的事实与形而上学》《现代资本主义问题中的历史和理论议题》《静力学和动力学:关于经济学中力学类比的质疑》《新经济学和经济活动的控制》等。', 2016, '奈特,埃米特,王去非,王文玉', '978-7-308-16219-7', '', 'F0-52', '', '浙江大学出版社', '2019-01-04 17:58:05', '', NULL, '1187688', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('625', '725', 'F063.1/L322', '服务特征的经济学分析', '服务经济学-经济分析', '本书内容包括：导论；服务产品的物理特征与市场特征；服务产品的使用价值特征与价值特征；服务的劳动生产率特征与价格特征；服务的国际贸易特征与国际投资特征等。', 2016, '李慧中', '978-7-309-12289-3', '', 'F063.1', '', '复旦大学出版社', '2019-01-04 17:58:05', '', NULL, '1143509', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('626', '726', 'F061.4/P2524', '福利经济学', '福利经济学', '本书从经济福利的角度探讨了在什么条件下私人产品和社会产品会发生差异,从而妨碍国民净产品的增加,进而提出了运用征税和津贴及国家干预等措施对之加以矫正的必要性和可能性。书中提出的的社会福利理论后来成为西方福利国家制定社会保障的政策依据,促进了西方福利国家社会保障制度的建立。', 2017, '皮古,金镝', '978-7-5080-9085-6', '', 'F061.4', '', '华夏出版社', '2019-01-04 17:58:05', '', NULL, '1200424', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('627', '727', 'F0/B273', '符号政治经济学批判:鲍德里亚早期思想研究', '鲍德里亚-政治经济学-研究,政治经济学-研究-法国-当代', '鲍德里亚是当代法国著名哲学家、社会学家,被学术界誉为消费社会批判及后现代研究的代表性人物之一。本书从发生学视角,通过对鲍德里亚早期著作的文本学解读,勾勒出其符号政治经济学批判理论的总体框架,并对其相关学说进行简要评论。其中,对于鲍德里亚消费社会批判及符号价值批判的梳理,是当前学术界热点也是本书的重点。', 2016, '宋德孝', '978-7-5520-1217-0', '', 'F0,B565.59', '', '上海社会科学院出版社', '2019-01-04 17:58:05', '', NULL, '1131988', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('628', '728', 'null', '告别沉默:新自由主义之后的文化与政治', '新自由主义(经济学)-研究', '本书分析了当代的发声危机，为发声证明。作者认为，仅仅研究发声是不够的，还需要认识发声的重要性。本书以积极的态度来分析新自由主义之后的文化与政治，指出了形成反新自由主义的原因，阐述了新自由主义思想中的矛盾，并强调了新自由主义的价值，为构建新的后新自由主义政治提供了资源。', 2017, '库尔德里,屈永刚', '978-7-5657-1996-7', '', 'F091.352', '', '中国传媒大学出版社', '2019-01-04 17:58:05', '', NULL, '1252349', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('629', '729', 'null', '告别宏微观架构的经济学', '经济学-研究', '本书最大的特色首先是改变了有经济学以来的经济学理论框架，无论是凯恩斯之前的古典、新古典，还是凯恩斯之后的宏微观，本书架构均与其大不同，直接从“大自然、资源”这些最基础最实在的概念入笔，在阐述了归纳为经济学四大基础规律之后，将经济学体系分为“增长的财富创造过程”及“财富分布的偏态与正态两种状态下的市场、经济与社会�', 2018, '廖博谛', '978-7-5196-0203-1', '', 'F0', '', '经济日报出版社', '2019-01-04 17:58:05', '', NULL, '1252642', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
