INSERT INTO `sys_book` VALUES ('65', '165', 'TP368.1/L328i', '单片机系统实践教程', '单片微型计算机-教材', '本教材以ATmega16单片机为蓝本，共分3篇，由15章组成。第1-3章为开发基础篇，主要介绍了AVR单片机的特点、开发编译环境以及C语言开发基础。第4-11章为实战演练篇，针对ATmega16单片机的数字I/O端口、中断系统、定时器/计数器、A/D转换、SPI总线、异步通信、TWI总线等功能模块设计典型实用实例，并对相应外围器件进行详细介绍，将知识讲解与实际应用充分�', 2017, '李擎,阎群', '978-7-03-052646-5', '', 'TP368.1', '', '科学出版社', '2019-01-04 17:57:47', '', NULL, '1214775', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('650', '750', 'null', '公共经济学教程', '公共经济学-高等学校-教材', '本书分基础篇、实践篇、拓展篇三个部分,系统论述了公共经济学的基本概念、理论和实践。基础篇主要论述公共经济学的基本概念及理论基础,涉及公共经济部门、政府经济职能及公共经济决策等内容;实践篇论及公共经济的五大领域,即卫生保健、国防经济、教育投资、养老保险和社会福利,并对公共收入与支出、公共服务外包等内容进行了重点阐释;', 2017, '秦立建', '978-7-300-23874-6', '', 'F062.6', '', '中国人民大学出版社', '2019-01-04 17:58:06', '', NULL, '1235290', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('651', '751', 'F062.6/Z696c', '公共经济学若干概念辨析', '公共经济学-研究', '与其他学科一样，公共经济学是由一整套概念体系及其内在逻辑关系构成的。然而，由于学科发展的历史不长、交叉性很强等原因，这一领域并非每一个概念都已臻于至善。有些“概念”缺失明确的定义；有些“概念”内涵不清；有些“概念”还只是习惯性的说法，缺乏严格的学术界定。本书对公共经济学领域中存有歧义的40个概念或术语逐一作了辨析。', 2016, '朱柏铭', '978-7-5095-6841-5', '', 'F062.6', '', '中国财政经济出版社', '2019-01-04 17:58:06', '', NULL, '1154156', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('652', '752', 'null', '公共经济与公共政策', '公共经济学-研究', '本书共分为2部分:一部分是重庆大学公共管理学院“湖畔沙龙”的部分内容摘要,另一部分是学术论文。', 2017, '', '978-7-5689-0869-6', '', 'F062.6', '', '重庆大学出版社', '2019-01-04 17:58:06', '', NULL, '1248012', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('653', '753', 'null', '公共物品的需求与供给', '公共经济学-研究', '本书以个人间的自愿交易为基础，探讨了公共物品的需求与供给问题。作者认为，公共物品的供求和私人物品的供求一样，是由社会成员自己而不是某种外生力量代为决定的。然而，公共物品的交易毕竟与私人物品的交易有着本质不同，在众多符合帕累托最优标准的公共支出一税收分配方案中，哪一种结果会为大多数社会成员所接受。需要借助于某种政�', 2017, '布坎南,马珺', '978-7-208-14648-8', '', 'F062.6', '', '上海人民出版社', '2019-01-04 17:58:06', '', NULL, '1231850', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('654', '754', 'null', '公共物品供给与政策研究', '产业经济学－文集', '本书拟汇集作者从2004-2017年内在包括SSCI、CSSCI等收录的知名学术期刊发表的一系列关于产业经济和公共政策领域的研究论文，以及作者为《文汇报》、《经济》、《环球时报》、《Global', 2017, '孙泽生', '978-7-5095-7797-4', '', 'F062.9-53', '', '中国财政经济出版社', '2019-01-04 17:58:06', '', NULL, '1255786', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('655', '755', 'F062.6-52/T1242', '公共选择:戈登•塔洛克论文集', '公共选择(经济学)-文集', '本书收集了戈登•塔洛克的52篇论文，其中少量论文是与他人合著的。这些论文按照主题，被分为九类：起源、多数决问题、需求显示过程、寻租、再分配政治学、官僚体制、社会的两难困难、社会成本问题、法与经济学。', 2015, '塔洛克,柏克,郑景胜', '978-7-100-11274-1', '', 'F062.6-52', '', '商务印书馆', '2019-01-04 17:58:06', '', NULL, '1131477', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('656', '756', 'A811.21/C337', '共产主义的原貌:马克思《1844年经济学哲学手稿》如是读', '《1844年经济学哲学手稿》-马克思著作研究', '本书主要选取了马克思的《1844年经济学哲学手稿》进行了专业解读。', 2016, '陈培永', '978-7-218-10844-5', '', 'A811.21', '', '广东人民出版社', '2019-01-04 17:58:06', '', NULL, '1187102', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('657', '757', 'F014.5/L867a', '共生经济:消费创富时代下的选择与生长', '消费经济学', '全世界的财富创造经过了三个主要阶段:第一阶段是生产致富。第二阶段是推销致富。第三阶段是消费致富,这是引导人们创造高质量生活、享受生活的最高生活方式和境界。即致富于消费之中,给人无压力,轻松致富。当我们成为消费商时,消费的同时帮助公司建立市场信息,我们就会拿市场信息费,我们提供市场信息不代表销售,也不代表服务,', 2017, '罗小林', '978-7-5096-5214-5', '', 'F014.5', '', '经济管理出版社', '2019-01-04 17:58:06', '', NULL, '1222321', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('658', '758', 'F120.2/L788', '共享发展的社会主义政治经济学', '中国特色社会主义-社会主义政治经济学-研究', '本书从共享利益的基本理论入手，对共享利益的内涵、特征、分类和发展规律加以明确，并从马克思主义学说、西方经济学说、当代国内经济学说等领域为共享利益探寻理论依据，进一步地，结合中国经济社会发展状况，对实现共享发展的八大举措展开全面、系统的分析，重点是指出存在的问题，并提出解决的思路。', 2016, '吕健', '978-7-309-12684-6', '', 'F120.2', '', '复旦大学出版社有限公司', '2019-01-04 17:58:06', '', NULL, '1183827', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('659', '759', 'F407.937/L589', '工程技术经济分析与估价', '建筑工程-工程经济学-高等学校-教材', '本书按照《建设工程工程量清单计价规范》(GB50500-2013)、《房屋建筑与装饰工程工程量计算规范》(GB50854-2013)、《建筑安装工程费用项目组成》(建标(2013)44号文)及《建筑工程建筑面积计算规范》(GB/T50353-2013)等文件和规范编写而成。全书共11章，主要内容包括概述、建筑工程经济评价要素、资金时间价值、技术方案经济效果评价、设备更新、价值工程、建设', 2016, '刘莉,赵亮', '978-7-5123-9313-4', '', 'F407.937-43', '', '中国电力出版社', '2019-01-04 17:58:06', '', NULL, '1145466', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
