INSERT INTO `sys_book` VALUES ('660', '760', 'F062.4/C3582', '工程经济教与学', '工程经济学-高等学校-教学参考资料', '本书共11章内容，主要内容：绪论、资金的时间价值、工程经济效果评价的方法、工程项目资金的筹集、工程项目不确定性分析、工程项目可行性研究、工程项目财务评价、工程项目国民经济评价、建筑设备更新方案的选择、价值工程、工程项目后评价。', 2017, '陈自然', '978-7-5682-3386-6', '', 'F062.4-42', '', '北京理工大学出版社', '2019-01-04 17:58:07', '', NULL, '1193881', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
