INSERT INTO `sys_book` VALUES ('662', '762', 'null', '工程经济学', '工程经济学-高等学校-教材', '本书主要围绕工程项目的投资决策问题展开。重点介绍工程项目的投资、经济效果、费用效益分析及投资选择的基本原则；针对工程项目特点及类型，系统地介绍不同决策结构下的评价方法和评价体系指标，及工程项目可行性研究的工作内容、工作重点及财务评价的基本方法、财务报表及评价指标。新版关注新的法规、政策，对涉及国家税制改革的相关�', 2017, '刘新梅', '978-7-301-28855-9', '', 'F062.4', '', '北京大学出版社', '2019-01-04 17:58:07', '', NULL, '1236435', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('663', '763', 'null', '工程经济学', '工程经济学-高等学校-教材', '本书根据工程管理专业的培养要求，介绍了工程经济学的基本原理和相关应用方法，主要内容包括：绪论、工程经济分析的基本要素、现金流量与资金时间价值、工程项目经济评价的指标和方法、工程项目经济评价不确定性与风险分析、工程项目财务评价、工程项目国民经济评价、设备更新的经济分析、价值工程和经济评价课程设计。', 2017, '石振武,苏义坤', '978-7-03-054087-4', '', 'F062.4', '', '科学出版社', '2019-01-04 17:58:07', '', NULL, '1236060', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('664', '764', 'null', '工程经济学', '工程经济学-高等学校-教材', '工程经济学是对工程技术问题进行经济分析的系统理论与方法。作为高等院校工程类专业的主干技术课程,工程经济学提供了一个对工程技术方案进行经济性分析与评价的系统框架,并协助工程师们进行经济、科学、有效的工程项目经济决策,具备较强的实践应用价值。', 2017, '梁学栋', '978-7-5096-5134-6', '', 'F062.4', '', '经济管理出版社', '2019-01-04 17:58:07', '', NULL, '1232130', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('665', '765', 'F062.4/W218b', '工程经济学', '工程经济学', '本书共分为12章,主要内容包括绪论、资金的时间价值与等值计算、工程项目的经济性评价指标体系、工程项目方案的经济性比较和选择、设备更新分析等。', 2017, '王少文,邵炜星', '978-7-5682-4182-3', '', 'F062.4', '', '北京理工大学出版社', '2019-01-04 17:58:07', '', NULL, '1227816', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('666', '766', 'F062.4/L754a', '工程经济学', '工程经济学-教材', '本教材在吸收国内外著作和教材精华、根据编者多年从事技术经济学的教学、科研工作所积累的经验基础上编写。全书以技术方案和经济效果的内在联系为基本出发点,结合我国财务制度、税收制度、投资体制和企业体制改革对技术经济学的新要求,以工程项目的技术经济评价为基本内容,形成了技术经济学完整的学科体系。', 2017, '陆菊春,徐莉', '978-7-302-47997-0', '', 'F062.4', '', '清华大学出版社', '2019-01-04 17:58:07', '', NULL, '1226206', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
