INSERT INTO `sys_book` VALUES ('67', '167', 'TP368.1/H153c', '单片机应用技术:基于C51和Proteus的项目设计与仿真', '单片微型计算机-高等学校-教材', '本书以ProteusISIS现代电子系统仿真技术为平台,通过构建系统原型实现硬件与软件的协同仿真。全书共15章,系统地介绍MCS-51系列单片机的硬件结构、KeiluVision4、Proteus仿真软件、C51语言基础与程序设计、定时与中断系统、显示、键盘、转换器、串行通信、电机等接口技术。', 2017, '韩克,薛迎霄', '978-7-302-45710-7', '', 'TP368.1-43', '', '清华大学出版社', '2019-01-04 17:57:47', '', NULL, '1204608', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('670', '770', 'F062.4/Z337', '工程经济学', '工程经济学-高等教育-教材', '全书分为十二章,主要内容包括工程经济学概论、工程经济基本要素、资金的时间价值与等值计算、工程项目经济评价方法、工程项目多方案评价、工程项目不确定性分析、工程项目可行性研究等。', 2016, '赵旭', '978-7-5123-9418-6', '', 'F062.4-43', '', '中国电力出版社', '2019-01-04 17:58:07', '', NULL, '1171019', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('671', '771', 'F062.4/H482', '工程经济学', '工程经济学', '本书是工程与经济的交叉学科,是研究工程技术实践活动经济效果的学科。即以工程项目为主体,以技术一经济系统为核心,研究如何有效利用资源,提高经济效益的学科。工程经济学研究各种工程技术方案的经济效益,研究各种技术在使用过程中如何以最小的投入获得预期产出或者说如何以等量的投入获得最大产出;', 2016, '胡斌', '978-7-302-42786-5', '', 'F062.4', '', '清华大学出版社', '2019-01-04 17:58:07', '', NULL, '1134588', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('672', '772', 'F062.4/L321a', '工程经济学理论与实务', '工程经济学-高等教育-教材', '本书主要介绍工程经济学绪论、现金流量及资金的时间价值、工程经济评价的基本要素、工程项目的确定性经济评价方法、投资方案选择、工程项目经济不确定性与风险分析、工程项目的可行性研究、工程项目的财务评价、工程项目的国民经济评价、公共项目的经济评价、设备项目的经济评价等方面内容。', 2016, '李国彦', '978-7-118-10874-3', '', 'F062.4-43', '', '国防工业出版社', '2019-01-04 17:58:07', '', NULL, '1162026', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('673', '773', 'F014.5/Z325', '购物学', '消费经济学-高等学校-教材', '本书共包括八章内容，集中研究了购物学的产生、购物者、购物环境、购物者心理、购物者自我概念和生活方式、购物活动过程、购物者选择模型以及购物的新动向。对该研究产生的背景、重要概念的界定、基础理论和模型、新兴购物现象等给出了清晰、明确的阐述。', 2016, '赵金蕊', '978-7-302-43748-2', '', 'F014.5', '', '清华大学出版社', '2019-01-04 17:58:07', '', NULL, '1145099', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('674', '774', 'null', '顾海兵自选集', '经济学-文集', '本书是作者21世纪的部分研究成果，内容涉及经济和社会问题，包括五个专题：关于发展规划的理论思考与实证分析、关于经济学的理论思考、宏观经济问题分析、国家经济安全问题分析、社会问题分析。', 2017, '顾海兵', '978-7-300-25020-5', '', 'F0-53', '', '中国人民大学出版社', '2019-01-04 17:58:07', '', NULL, '1243823', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('675', '775', 'F038/F335', '寡头垄断的动态模型:理论与应用经济学基础', '垄断经济学', '《寡头垄断的动态模型》对动态寡头垄断的最前沿研究进行了相对系统的梳理和呈现,集中关注了寡头垄断市场中策略性关系的描述和研究,以及在持续演进的动态情况下的经济状况与企业之间的竞争状况。', 2016, '弗登博格,泰勒尔,张嫚,解晓爽', '978-7-111-54873-7', '', 'F038', '', '机械工业出版社', '2019-01-04 17:58:07', '', NULL, '1161240', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('676', '776', 'null', '怪诞行为学:可预测的非理性', '行为经济学－通俗读物', '杜克大学行为经济学家丹•艾瑞里以生活做实验室，拿真人当小白鼠，用大量新颖有趣的实验，彻底颠覆“经济人”的主流观点，带领我们探索隐藏在人类行为和决策背后的隐形力量——“可预测的非理性”。在本书的增订部分中，针对非理性如何影响我们的日常生活，并导致2008年金融危机，作者从行为经济学的角度出发，提供了自己独到的见解，并告�', 2017, '', '978-7-5086-8096-5', '', 'F069.9-49', '', '中信出版社', '2019-01-04 17:58:08', '', NULL, '1250342', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('677', '777', 'null', '怪诞行为学', '行为经济学-通俗读物', '本书内容：按照理性的观点，我们的一切决策应该符合我们的最大利益，我们应该辨别面临的各种选择并准确计算价值，做出符合我们最大利益的选择。如果我们非常理性地做到各种“应该”，那真的再好不过了。不幸的是，我们做不到。在《怪诞行为学：可预测的非理性》中，行为经济学家艾瑞里探讨了人类偏见的负面影响,而本书则重点探讨了非理性�', 2017, '艾瑞里,赵德亮', '978-7-5086-8143-6', '', 'F069.9-49', '', '中信出版集团股份有限公司', '2019-01-04 17:58:08', '', NULL, '1250825', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('678', '778', 'null', '怪诞行为学', '行为经济学－通俗读物', '该书为通俗经济学作品。作者艾瑞里从成本—收益分析举例入手，试图找出高收益会驱使人们更大胆地去“犯错”这一论点的漏洞。通过多个行为实验，艾瑞里有力地证明：成本—收益在解释人为何会撒谎这一问题时，并不正确。我们都想让自己看起来诚实可信；我们都有为一己私利而欺骗他人的本能，但同时还会给自己贴上“诚实的好人”的标签；我�', 2017, '', '978-7-5086-8148-1', '', 'F069.9-49', '', '中信出版社', '2019-01-04 17:58:08', '', NULL, '1250544', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
