INSERT INTO `sys_book` VALUES ('698', '798', 'null', '国际宏观经济学', '国际经济学-宏观经济学-教材', '本书分为四篇共11章：第一部分（第1章）国际宏观经济学导论简要介绍本书涉及的三大议题：汇率、国际收支及相关应用和政策问题。第二部分（第2-4章）首先介绍与汇率和外汇市场有关的基础知识，转而分别论述长期汇率决定的货币理论和短期汇率决定的资产理论，并进一步说明二者是如何融合为一个完整的汇率决定理论，以及这个完整的理论在不同�', 2017, '罗伯特•C.芬斯特拉,艾伦•M.泰勒', '978-7-300-25326-8', '', 'F015', '', '中国人民大学出版社', '2019-01-04 17:58:08', '', NULL, '1260879', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
