INSERT INTO `sys_book` VALUES ('707', '807', 'F11-0/L3244', '国际经济学', '国际经济学-高等学校-教材', '本书以国际经济关系为研究对象，系统介绍国际经济中各经济体之间经济联系的内在机制、影响及其政策含义。本书以微观经济学和宏观经济学为理论基础，在系统介绍国际经济学的理论和政策的同时，注重讲解规范的经济学思维方式和分析方法。', 2017, '李坤望', '978-7-04-045943-2', '', 'F11-0', '', '高等教育出版社', '2019-01-04 17:58:09', '', NULL, '1191742', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('708', '808', 'F11-0/R1993', '国际经济学', '国际经济学-高等学校-教材', '本书共三篇九章，大体分为国际贸易、国际金融和国际金融关系三部分。具体来讲，第一篇论述了国际贸易产生的原因及发展历程、国际贸易理论的分类和国际贸易政策工具；第二篇论述了汇率和国际收支的相关内容，如国际商品交换的原因、国际收支、外汇市场和国际货币体系；第三篇为国际经济关系，包括生产要素的国际流动、国际传导机制和国际�', 2017, '任治君,吴晓东', '978-7-5504-2550-7', '', 'F11-0', '', '西南财经大学出版社', '2019-01-04 17:58:09', '', NULL, '1190600', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
