INSERT INTO `sys_book` VALUES ('710', '810', 'F11-0/F142', '国际经济学', '国际经济学-高等学校-教材', '本书是一本集知识性、学术性和通俗性于一体的国际经济学教材,全面介绍了国际经济理论的发展和演变主要包括国际贸易理论和国际贸易政策的历史演变、国际收支及其调节、汇率及其决定、国际货币体系等内容。本书突出了新、易、例的特点,以本科教育为主要对象,做到内容深入浅出,文图结合,理论与实践相结合,', 2016, '樊安群,徐雪竹,邵李津,肖海霞,李争辉', '978-7-302-44388-9', '', 'F11-0', '', '清华大学出版社', '2019-01-04 17:58:09', '', NULL, '1150699', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
