INSERT INTO `sys_book` VALUES ('716', '816', 'F831.6/Z318', '国际投资学国际理论前沿', '国际投资-投资经济学', '本书稿主要从国外研究、国内研究两个角度，以及理论与政策两个方面探究国际投资学的意义。一方面从全球视角看世界,分析了国际直接投资的理论和政策前沿;另一方面,从中国视角看全球,研究中国双向投资新格局下所涉及的国际直接投资理论和政策前沿。', 2017, '赵蓓文', '978-7-5520-1947-6', '', 'F831.6', '', '上海社会科学院出版社', '2019-01-04 17:58:09', '', NULL, '1205602', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
