INSERT INTO `sys_book` VALUES ('725', '825', 'null', '海航集团第二届东西方经济文化高峰论坛文集', '文化经济学-文集', '本书是由海航集团举办的“第二届东西方经济文化高峰论坛”中,与会专家的演讲和研讨内容整理编辑而成。参与此次论坛的有李稻葵、胡鞍钢、皮萨里德斯等国内外著名经济学家,王守常、熊召政等知名文化学者,以及海航集团董事局主席陈峰、银湖集团执行董事长吴炳炜等商业人士。专家们围绕“开放交流、圆融东西”的主题,从自身专业角度,', 2017, '逯鹰', '978-7-5086-7028-7', '', 'G05-53', '', '中信出版社', '2019-01-04 17:58:09', '', NULL, '1183277', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('726', '826', 'P74/Y496b', '海洋产业经济研究:从主流框架到前沿问题', '海洋经济学-产业经济学-研究', '海洋产业经济具有悠久的发展历史，海洋渔业、海洋盐业、海洋交通运输业是古老的产业经济之一，然而海洋产业经济研究的历史仅有半个世纪。20世纪60年代以来，随着海洋资源开发的迅速发展和新兴海洋产业的日益兴起，海洋产业经济作为学术研究的一个分支领域得形成与发展。', 2016, '于谨凯', '978-7-5141-6061-1', '', 'P74', '', '经济科学出版社', '2019-01-04 17:58:09', '', NULL, '1160541', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
