INSERT INTO `sys_book` VALUES ('732', '832', 'null', '韩玉军自选集', '经济学-文集', '本书分为两部分，收录了《入世与中国的旅游业》《创立服务劳动价值论势在必行》《服务业外商直接投资研究的现状与进展》《全球金融危机与中国服务贸易的开放》《中国服务业发展的国际比较》等文章。', 2017, '韩玉军', '978-7-300-25045-8', '', 'F0-53', '', '中国人民大学出版社', '2019-01-04 17:58:09', '', NULL, '1243834', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
