INSERT INTO `sys_book` VALUES ('734', '834', 'F0-49/C171', '好奇心经济学:像侦探小说一样有趣的经济学故事', '经济学-通俗读物', '本书对历史、文学、社会中的各种故事和现象从经济学的角度进行了通俗化解读，将经济学知识引入工作、家庭生活、心理学、历史学、体育比赛、投资学等领域，用生动的故事讲述了各个领域发生的事件都可以用看似枯燥的经济学知识来解读，让读者在趣味阅读的同时，增加了对事件的理性分析。', 2016, '岑嵘', '978-7-308-16238-8', '', 'F0-49', '', '浙江大学出版社', '2019-01-04 17:58:10', '', NULL, '1177506', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('735', '835', 'F0-0/H317b', '何祚庥论马克思主义经济学', '马克思主义政治经济学-研究', '本书收集了除附录和代前言以外共17篇作者的文章,内容包括:科学发展观和捍卫国家安全、马克思主义政治经济学也要“与时俱进”、科学创新:发展马克思主义政治经济学的必由之路、必须将科技进步引入马克思主义政治经济学的定量研究、一个可将劳动价值论和边际效用论统一在一个方案的数理经济模型等。', 2016, '何祚庥,庆承瑞', '978-7-5638-2524-0', '', 'F0-0', '', '首都经济贸易大学出版社', '2019-01-04 17:58:10', '', NULL, '1161390', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('736', '836', 'null', '合适:从升学择校、相亲配对、牌照拍卖了解新兴实用经济学', '市场经济学-通俗读物', '本书介绍了经济学的一个领域——市场设计。“市场设计”，就是制定好的规则，得出最有效率的结果，将东西分配给最合适的人。这个理论获得了2012年诺贝尔经济学奖。全书分三个部分依次透析了单边匹配、双边匹配和拍卖问题的内在逻辑，令读者茅塞顿开。', 2016, '坂井丰贵,蔡晓智', '978-7-210-08768-7', '', 'F014.3-49', '', '江西人民出版社', '2019-01-04 17:58:10', '', NULL, '1172783', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('737', '837', 'F014.1/K284', '合作论', '合作经济学', '《合作论》内容分为绪言、历史、合作分配、合作生产、劳工合伙与红利分享、农业合作、大陆上的合作者、教育事业、结论，共九章。此书为商务印书馆1924年出版版本，为西学汉译中的一部，对民国时期的西方经济研究产生过重要影响。', 2016, '克莱顿,徐渭津', '978-7-5520-1161-6', '', 'F014.1', '', '上海社会科学院出版社', '2019-01-04 17:58:10', '', NULL, '1130704', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
