INSERT INTO `sys_book` VALUES ('742', '842', 'null', '宏观经济学:政策与实践', '宏观经济学-教材', '本书内容全面，既阐述了关于长期经济增长的理论，又讨论了总产出、失业和通货膨胀的短期经济波动；既有对宏观经济理论的介绍，又讨论了宏观经济模型的微观经济基础，还讨论了基于对宏观经济分析的微观基础更多的关注而建立的最新的经济周期理论。最后的结束语总结了宏观经济学家对宏观经济政策实施存在的一致看法和分岐，对读者回顾全书�', 2018, '米什金,杨澄宇', '978-7-111-56741-7', '', 'F015', '', '机械工业出版社', '2019-01-04 17:58:10', '', NULL, '1250822', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('743', '843', 'F015/X331', '宏观经济学', '宏观经济学-高等学校-教材', '宏观经济学主要研究人类社会经济活动的总体表现,如国民总产值、通货膨胀率、失业率等变量的运行规律以及它们之间的关系。本书在借鉴国内外优秀教材的基础上,力求使经济学与中国现实相结合。作者在提供了宏观经济学基本概念的阐释之外,还引入了对现在主流宏观经济学流派和研究前沿的介绍,既巩固了学生的基础知识,', 2017, '谢玉梅,浦徐进,王敏琴', '978-7-5432-2760-6', '', 'F015-43', '', '格致出版社:上海人民出版社', '2019-01-04 17:58:10', '', NULL, '1231183', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
