INSERT INTO `sys_book` VALUES ('753', '853', 'F015/R1952', '宏观经济学', '宏观经济学-教材', '本书针对国外原版教材以美国案例为主、难以解释国内宏观经济问题以及国内一些教材以介绍为主、缺乏应用性的缺陷，跟踪当代西方经济学的前沿进展，根据宏观经济学的基础、长期模型、短期模型、模型的扩展、宏观经济问题与政策五大部分建立课程体系结构。', 2016, '任保平,宋宇', '978-7-03-049725-3', '', 'F015', '', '科学出版社', '2019-01-04 17:58:10', '', NULL, '1144613', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
