INSERT INTO `sys_book` VALUES ('77', '177', 'TP368.1/Y192a', '单片机原理及应用:基于C51的Proteus项目仿真案例', '单片微型计算机-高等学校-教材', '为了让同学们对单片机课程有深刻的认识，我采用了独创五位一体教学方式，就是生活-机制-机构-接口-应用五个层次进行讲解。主题思想就是单片机的设计就是从生活中提炼机制，然后设计结构去体现机制，设计者在设计结构的过程中为了留了很多控制接口，我们学习单片机的任务就是熟悉单片机的结构和实现的机制的基础上，利用单片机结构的接口去�', 2017, '杨保亮,杨守良', '978-7-303-22807-2', '', 'TP368.1-43', '', '北京师范大学出版社', '2019-01-04 17:57:48', '', NULL, '1238895', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('770', '870', 'X196/T141a4', '环境与自然资源经济学', '环境经济学,自然资源-资源经济学', '本书通过完整的理论分析和有力的经验证据，清楚地向人们阐述了当今世界面临的复杂的环境和自然资源问题，并详细讨论了保护环境和自然资源、实现可持续发展的有关政策问题。', 2016, '泰坦伯格,刘易斯,王晓霞,石磊,安树民,杨鹂', '978-7-300-22900-3', '', 'X196,F062.1', '', '中国人民大学出版社', '2019-01-04 17:58:11', '', NULL, '1160999', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('771', '871', 'X196/J427', '环境与自然资源经济学导引与案例', '环境经济学-高等学校-教材,自然资源-资源经济学-高等学校-教材', '本书共14章，内容涵盖环境与自然资源经济学的基本理论和主要内容,包括环境价值评估的原理和方法、人口问题、非再生资源、可再生资源、水资源与土地资源、污染控制经济学、环境与贸易、可持续发展等。每一章分为“导引”和“案例”两大部分：“导引”简明扼要地阐述本章设计的主要理论和知识点;“案例”根据该章节的内容选编3-5个案例,', 2017, '焦露', '978-7-5141-7884-5', '', 'X196-43,F062.1-43', '', '经济科学出版社', '2019-01-04 17:58:11', '', NULL, '1224153', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
