INSERT INTO `sys_book` VALUES ('792', '892', 'F821.0/F337', '货币政治:汇率政策的政治经济学', '货币政策-政治经济学-研究', '货币及其价值是世界经济的核心，影响着国际贸易、投资、金融、移民及旅行，而汇率制度往往定义了国际经济秩序。弗里登评估了各个历史时期政治和货币之间的关系，分析了欧洲货币一体化的政治经济学以及拉丁美洲国家过去40年的货币政策，阐释了各种汇率政策的作用。融合了数据和案例的分析，阐明了政治与经济如何影响和决定汇率政策。本书第', 2016, '弗里登,孙丹,刘东旭,王颖樑', '978-7-111-53472-3', '', 'F821.0', '', '机械工业出版社', '2019-01-04 17:58:11', '', NULL, '1133964', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('793', '893', 'F224.0/K158', '几何经济学', '数理经济学', '本书内分几何图式的用途、需要曲线、供给曲线和价格的决定、曲线的变形和马歇尔氏曲线、经济学的代数研究等11章。论述运用几何学方法，解释商品需求与交换定律的理论和应用方法，对马歇尔的几何经济学理论详加介绍和分析，并提出多处修正意见。', 2016, '坎宁安,李植泉', '978-7-5520-1205-7', '', 'F224.0', '', '上海社会科学院出版社', '2019-01-04 17:58:11', '', NULL, '1130695', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
