INSERT INTO `sys_book` VALUES ('804', '904', 'F062.4/W1963', '技术经济学', '技术经济学-高等学校-教材', '全书分为四篇,第一篇介绍技术经济学相关的基础知识,第二篇介绍评价常用方法的基本理论,第三篇介绍其知识应用,第四篇是技术经济学发展内容。本书参阅了大量文献,内容体系完整,知识丰富,可以满足工科学生技术经济学、管理学、财会等方面基础及扩展知识的需要。', 2016, '王凤科', '978-7-305-17458-2', '', 'F062.4-43', '', '南京大学出版社', '2019-01-04 17:58:12', '', NULL, '1147978', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
