INSERT INTO `sys_book` VALUES ('807', '907', 'F224.0-39/G2363', '计量经济分析方法与建模:EViews应用及实例', '计量经济学-经济分析,计量经济模型', '本书全面介绍计量经济学的主要理论和方法，在数学描述方面适当淡化，以讲清楚方法、思路为目标，不做大量的推导和证明，重点放在如何运用各种计量经济方法对实际的经济问题进行分析、建模、预测、模拟等实际操作上。本书中的实际案例大多数是作者在实践中运用的实例和国内外的经典实例，并基于EViews软件来介绍实际应用，具有很强的可操作性。', 2016, '高铁梅', '978-7-302-46100-5', '', 'F224.0-39', '', '清华大学出版社', '2019-01-04 17:58:12', '', NULL, '1198078', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
