INSERT INTO `sys_book` VALUES ('821', '921', 'F224.0/Z323a5', '计量经济学', '计量经济学-高等教育-教材', '本书旨在使学生理解计量经济模型思想，掌握常用的计量经济模型。同时研究计量经济模型在经济领域中分析问题以及辅助决策的作用和功能。本书通过各类经济问题训练学生的数量化基本技能，提高学生数学分析水平，尤其是培养学生对各类经济问题的研究能力及综合分析的能力。本书不仅仅是介绍计量经济学的基础理论，而且训练学生参与计量经济�', 2016, '赵国庆', '978-7-300-22897-6', '', 'F224.0-43', '', '中国人民大学出版社', '2019-01-04 17:58:12', '', NULL, '1141589', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('822', '922', 'F224.0/F371', '计量经济学基础', '计量经济学-高等学校-教材', '本书作为计量经济学的入门教材,从经管类专业本科教学的实际出发,强调方法应用,注重计量案例讲解。全书共9章,包括统计基础知识回顾、经典回归模型、违背经典假设情形的建模、模型的诊断以及两个计量专题—离散数据和面板数据的建模。书中各章都穿插了相应例子,并给出了EViews软件的操作结果,便于读者对理论模型的理解与掌握。', 2016, '付宏,尹康', '978-7-111-54494-4', '', 'F224.0', '', '机械工业出版社', '2019-01-04 17:58:12', '', NULL, '1150627', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('823', '923', 'null', '计量经济学简明教程', '计量经济学-高等学校-教材', '本书共分为七章,主要内容包括:绪论;一元线性回归模型;多元线性回归模型;放宽基本假定的回归模型;专门问题;联立方程模型;计量经济学应用模型。', 2018, '廖明球', '978-7-5638-2728-2', '', 'F224.0', '', '首都经济贸易大学出版社', '2019-01-04 17:58:13', '', NULL, '1256097', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
