INSERT INTO `sys_book` VALUES ('835', '935', 'F063.4/T337', '家庭负担系数研究', '家庭经济学-研究', '本书在厘清家庭范畴的基础之上，依据有效性、实用性、便捷性和可靠性的设计原则，按照人口指标与负担指标相结合、基本指标与修正指标相结合、客观指标与主观指标相结合的研究思路，研究和设计了能够简洁明了体现家庭负担状况且方便使用的家庭负担系数。', 2016, '田丰', '978-7-5097-8730-4', '', 'F063.4', '', '社会科学文献出版社', '2019-01-04 17:58:13', '', NULL, '1142176', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('836', '936', 'null', '贾根良自选集', '经济学-文集', '本书分为中国经济学的自主创新、新经济思想史、中国新李斯特学派、演化经济学的创造性综合四篇，收录了《中国经济学发展的西方主流化遭遇重大质疑》《中国经济学革命论》《“新经济思想史”刍议》《打造中国的经济思想史学派》《政府干预：中日大分流的关键》等文章。', 2017, '贾根良', '978-7-300-25028-1', '', 'F0-53', '', '中国人民大学出版社', '2019-01-04 17:58:13', '', NULL, '1243833', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
