INSERT INTO `sys_book` VALUES ('839', '939', 'F407.9/X173', '建筑工程经济', '建筑经济学-工程经济学-高等教育-教材', '本书针对工程建设的整个过程,系统地介绍了建筑工程经济的基本理论、方法与实验,主要内容包括工程建设程序、现金流量与资金时间价值计算、建设项目经济效果分析、建筑项目经济评价的不确定性分析等。', 2017, '夏才安', '978-7-122-29554-5', '', 'F407.9-43', '', '化学工业出版社', '2019-01-04 17:58:13', '', NULL, '1217349', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('84', '184', 'null', '单片机原理与应用', '单片微型计算机', '本书立足于MCS-51经典结构，以AT89S52单片机为应用对象深入浅出的讲述单片机及应用系统设计原理，编程语言采用C51，并加入单片机实验和课程设计，以及protues和keil联调应用在单片机的仿真。', 2017, '', '978-7-5661-1266-8', '', 'TP368.1', '', '哈尔滨工程大学出版社', '2019-01-04 17:57:48', '', NULL, '1245646', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('840', '940', 'F407.9/W213a', '建筑市场经济学研究', '建筑经济学-研究', '本书是在作者多年研究和教学基础上形成的，是关于建筑市场经济学的深入思考和探索，提出了建筑市场经济学的理论框架，包括建筑市场体系与运行机制、建筑市场供需与价格、建筑市场结构、建筑市场行为与绩效等。', 2016, '王孟钧,戴若林', '978-7-112-18640-2', '', 'F407.9', '', '中国建筑工业出版社', '2019-01-04 17:58:13', '', NULL, '1133904', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('841', '941', 'F0-08/L611', '简明西方经济学', '西方经济学-高等教育-教材', '西方经济学的学科体系分两大部分，即微观经济学和宏观经济学。微观经济学主要绍市场经济理论，体系的构建完成于20世纪30年代以前，主要包括：价格理论、消费均衡理论、生产者理论、成本理论、市场理论、生产要素定价理论、市场失灵及微观经济政策理论等。宏观经济学是在20世纪30年代以后逐渐形成和发展起来，主要是政府干预主义的理论内容，', 2016, '刘旭,刘建廷', '978-7-5184-0962-4', '', 'F0-08', '', '中国轻工业出版社', '2019-01-04 17:58:13', '', NULL, '1157029', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('842', '942', 'C53/J345/(12)', '江苏社科名家文库,刘志彪卷', '社会科学-文集,经济学-文集', '本书介绍江苏社科名家、著名经济学家刘志彪教授。他以产业经济学、长三角区域经济、中国经济发展作为主要研究领域,积极为国家经济发展建言献策,是“江苏省委省政府决策的智库、智囊、参谋、中介和桥梁”。本卷书共37万字,力争在有限的文字内,凝聚刘志彪教授的学术精华,展现其治学风范。框架结构主要分为三大块:1.', 2017, '刘志彪', '978-7-214-18790-1', '', 'C53,F0-52', '', '江苏人民出版社', '2019-01-04 17:58:13', '', NULL, '1204123', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('843', '943', 'null', '交易费用与公共经济', '交易-费用-研究,公共经济学-研究', '本书以政府财政收支与管制为研究对象，在传统经济学分析的基础上，加入交易费用约束，重新审视公共经济研究领域的各大课题。本书分为两大部分，第一部分为基础理论部分，为政府的存在与收支行为提供理论依据，并探讨政府的性质与边界。第二部分为政府收支实践与管制政策，研究政府的收入再分配、扶贫、投资、税收等行为对市场主体的影响�', 2017, '周燕', '978-7-5117-3399-3', '', 'F062.6,F713.1', '', '中央编译出版社', '2019-01-04 17:58:13', '', NULL, '1232056', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
