INSERT INTO `sys_book` VALUES ('845', '945', 'null', '教学改革实践之对分课堂实施与学习品质优化', '政治经济学－课堂教学－教学研究－高等学校', '以复旦大学张学新教授的“对分课堂”理论为指导，同时融入作者多年来优化学生学习品质的探索，应用于《政治经济学》教学实践的一些成果。本书主要包括两部分内容：一是结合“对分课堂”的理论要求，展示《政治经济学》各个章节的精讲留白、消化吸收、分组讨论、讨论点评与讨论总结等关键性教学环节。二是优化学生学习品质的探索，展示学�', 2017, '', '978-7-116-10360-3', '', 'F0', '', '地质出版社', '2019-01-04 17:58:13', '', NULL, '1261999', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
