INSERT INTO `sys_book` VALUES ('847', '947', 'G40-054/L4612', '教育财政学', '教育经济学-财政学', '本书从教育财政及相关概念入手，介绍了教育财政的规模和支出原则，联系经济学方法论与教育财政学的关系，从教育财政的成本分类、教育财政与经济增长、教育财政与人力资源及发展、教育财政资源配置绩效评价、教育财政体制、以及政府和非政府教育投入等方面。', 2016, '廖楚晖', '978-7-301-27664-8', '', 'G40-054,G467', '', '北京大学出版社', '2019-01-04 17:58:13', '', NULL, '1173386', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
