INSERT INTO `sys_book` VALUES ('863', '963', 'G40-054/Z173', '教育经济与管理', '教育经济学,教育管理学', '本文将从高等教育经济学、管理学的基本概念来对教育经济与管理的内涵进行总结和概括，结合案例对教育经济与管理的基本内容，即教育与经济的关系、教育与劳动力市场的关系、教育管理的信息化、知识创新与管理以及教育人力资源的开发利用等方面进行分析研究，并探讨我国目前教育经济与管理学科发展所面临的困境与出路。教育经济与管理在现�', 2016, '曾嘉,黄荣晓,黄英女', '978-7-5194-1382-8', '', 'G40-054,G40-058', '', '光明日报出版社', '2019-01-04 17:58:14', '', NULL, '1213106', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
