INSERT INTO `sys_book` VALUES ('866', '966', 'G475/H133', '教育行业微营销实战攻略', '学校管理-网络营销,教育经济学', '本书从“案例+技巧”两条线展开，帮助读者快速成为教育微营销的高手。内容涉及慕课在线、微课在线、腾讯教育、阿里巴巴教育、百度教育、新东方教育、100教育、网易公开课、学而思教育、学大教育等30多家机构的经典案例。', 2017, '', '978-7-302-46171-5', '', 'G475,G40-054', '', '清华大学出版社', '2019-01-04 17:58:14', '', NULL, '1202820', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('867', '967', 'G40-054/Z321', '教育与金融、保险', '教育经济学-教材,教育-保险-教材', '本书分上、下两篇。上篇对现代金融/财务的基本知识及其可能对教育发展的支持作用作了简明的讲解；下篇主要阐明的问题是如何采用科学的方法来处理教育领域所面临的风险事故以获得相对较合理、经济、充足的保障。', 2000, '赵冬青,陈丽霞', '7-04-008535-6', '', 'G40-054', '', '高等教育出版社', '2019-01-04 17:58:14', '', NULL, '1214599', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('868', '968', 'G40-054/J539d', '教育资本:规范与运作', '教育经济学', '本书研究教育产业资本，针对目前教育的发展回答如何进一步改革，并对教育资本进行规范与运作。基本思路是改革的，具有前瞻性、可操作性，为政府决策提供了理论依据和量化分析。', 2003, '靳希斌', '7-5408-3800-0', '', 'G40-054', '', '四川教育出版社', '2019-01-04 17:58:14', '', NULL, '1216778', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
