INSERT INTO `sys_book` VALUES ('903', '1003', 'F091.34/B555c', '经济学的边际革命:说明和评价', '边际效用学派-经济学-研究', '本书是西方经济学说史方面的一部专题论文集，收录了各国著名经济学家对边际经济理论和马克思主义经济理论发展的见解。共收录论文18篇，既论述了英国古典政治经济滨衰落，也讨论了奥地利学派和洛桑学派的兴起，尤其是对边际主义的起源问题作了比较详细的探讨。', 2016, '布莱克,科茨,古德温,于树生', '978-7-100-12599-4', '', 'F091.34', '', '商务印书馆', '2019-01-04 17:58:15', '', NULL, '1172074', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('904', '1004', 'null', '经济学的尺度', '经济学－文集', '本书是作者近几年公开发表的经济学文章的作品集，以2014年到2017年宏观经济运行为线索，围绕金融发展、人民币汇率、去杠杆、结构性改革、互联网经济、世界经济新格局等经济转型期的突出现象和热点话题展开讨论。本书的特点之一是，不停留在宏观经济现象本身，而是致力于探讨宏观现象背后的微观基础，并在对经济逻辑进行深入分析的基础上提出', 2018, '', '978-7-5454-5203-7', '', 'F0-53', '', '广东经济出版社', '2019-01-04 17:58:15', '', NULL, '1261511', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
