INSERT INTO `sys_book` VALUES ('913', '1013', 'F0/M137', '经济学基础', '经济学-教材', '本书用一种较为轻松的方式将微观经济学和宏观经济学中最为基础的部分介绍给你，包括经济学的本质、稀缺性与选择、供给与需求、生产、成本、利润、商业周期、货币、税收、国际贸易等内容；同时也涉及了一些当今世界广受关注的经济话题，包括货币政策、垄断、劳动力市场，以及最近的金融危机和房地产市场危机。', 2017, '马斯切纳,陈宇峰,姜井勇', '978-7-300-22607-1', '', 'F0', '', '中国人民大学出版社', '2019-01-04 17:58:15', '', NULL, '1203162', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
