INSERT INTO `sys_book` VALUES ('915', '1015', 'F0/G211', '经济学基础', '经济学-高等学校-教材', '本书主要内容分为两大部分--微观经济学和宏观经济学,共15个项目。其中项目一为概论,主要介绍了西方经济学的发展过程和基本知识;项目二至项目十为微观经济学部分,分别介绍了均衡价格理论、弹性理论、消费者行为理论、生产者行为理论、市场理论、分配理论、福利经济学和市场失灵的相关知识;项目十一至十五为宏观部分,', 2017, '高博,王菲', '978-7-302-42750-6', '', 'F0-43', '', '清华大学出版社', '2019-01-04 17:58:15', '', NULL, '1185978', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
