INSERT INTO `sys_book` VALUES ('917', '1017', 'F0/Y391a', '经济学基础问答', '经济学-高等学校-教材', '本书以问答形式深入浅出地叙述了微观、宏观和国际经济学的基本理论知识。全书共21章,总计383个问题,篇幅虽然不大,但内容比较全面,涵盖了微观经济学、宏观经济学和部分国际经济学的主要理论,也包含了一些作者对社会经济热点问题的看法(如社会流动性对经济发展的影响)。最后一章还探讨了如何借鉴西方经济学研究我国改革开放和经济发展的问题。', 2016, '尹伯成,刘江会', '978-7-300-23145-7', '', 'F0', '', '中国人民大学出版社', '2019-01-04 17:58:15', '', NULL, '1163732', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('918', '1018', 'null', '经济学家和说教者', '经济学-研究', '本书中施蒂格勒以其温文而雅的幽默感、机智的戏谑以及清晰的思路，在回顾经济史的过程中检验了经济学家的重要性，以及经济学家期望对公共政策有所影响的坚定信念。他认为，经济学家并不是毫无贡献，但是他们的贡献是“微小而不易察觉的”，他们已经从早期的道德说教转向科学地说明。经济学家努力以科学家的客观性来探讨它们的主题，他们�', 2018, '施蒂格勒,贝多广,刘泸生,郭治薇', '978-7-5432-2819-1', '', 'F0', '', '格致出版社[等]', '2019-01-04 17:58:15', '', NULL, '1260526', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
