INSERT INTO `sys_book` VALUES ('92', '192', 'null', '单片机原理与应用实例教程', '单片微型计算机-高等教育-教材', '本书共分11章，内容包括：微型计算机基础知识、MCS-51系列单片机芯片结构、MCS-51系列单片机指令系统、汇编语言程序设计、MCS-51系列单片机存储器扩展技术、MCS-51系列单片机的中断与定时系统等。', 2017, '黄灿胜', '978-7-113-23366-2', '', 'TP368.1', '', '中国铁道出版社', '2019-01-04 17:57:48', '', NULL, '1255128', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('920', '1020', 'null', '经济学教程', '经济学-高等学校-教材', '本书在内容安排上力图能够涵盖本科生必须掌握的经济学基础知识,并在对其给予系统、准确阐述的同时,对有关理论研究进行了深入和拓展,为读者提供了一个由浅入深的学习思考路径。作为一本实践性、应用性较强的的经济学教科书,本书力求结构合理,内容完善,概念描述准确,尽量避免使用复杂的数学工具,结合实际对现实经济现象进行分析。', 2018, '姜国刚,赵东安', '978-7-302-48945-0', '', 'F0', '', '清华大学出版社', '2019-01-04 17:58:16', '', NULL, '1260546', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('921', '1021', 'null', '经济学精要', '经济学-教材', '本书是一本典型的初级经济学教材,至今已出版六版,广受读者好评。全书用通俗易懂的方式,把宏观经济学和微观经济学的基本原理、基本理论和模型进行了详细介绍,并通过运用大量的例子和资料,帮助读者加深对经济学理论的理解。', 2018, '巴德,王秋石', '978-7-300-24749-6', '', 'F0', '', '中国人民大学出版社', '2019-01-04 17:58:16', '', NULL, '1255792', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('922', '1022', 'F0/S655b', '经济学精要', '经济学-教材', '本书分为：导论；微观经济学；宏观经济学和世界经济四部分。主要内容有：经济学基本知识；市场、需求和供给；市场动作；供给决策；市场结构；工资和收入分配；市场失灵和政府政策；总需求及国民经济；总需求及经济增长；银行、货币及利率；通货膨胀和失业；宏观经济政策；全球化和国际贸易；国际收支与汇率等。', 2017, '斯洛曼,加勒特,吕宏', '978-7-302-46163-0', '', 'F0', '', '清华大学出版社', '2019-01-04 17:58:16', '', NULL, '1225110', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('923', '1023', 'null', '经济学科教育教学思想改革与实践研究', '经济学-教学研究', '本书分教学方法改革、教学管理与思想、实践教学探索、创新人才培养、课程改革与专业建设。内容包括：微观经济学案例教学法应用研究；论高校教育创新；高校经管类课程网络交互教学模式研究；福利经济学教法探析；大数据背景下统计学教育的思考等。', 2017, '项义军', '978-7-5150-2013-6', '', 'F0-42', '', '国家行政学院出版社', '2019-01-04 17:58:16', '', NULL, '1239581', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('924', '1024', 'F0-52/L691', '经济学历史学法学研究', '经济学-文集,法学-文集,史学-文集', '本书收录了作者从1969年以来各时期的主要论文，涉及经济学、法学、历史学、社会学等方面，按时间顺序分为五编，包括《论从原始社会到封建社会的发展》《论政治经济学研究的对象和任务》《论商品的二因素与劳动的重要性》等文。', 2016, '卢耸岗', '978-7-220-09915-1', '', 'F0-52,K0-52', '', '四川人民出版社', '2019-01-04 17:58:16', '', NULL, '1218831', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('925', '1025', 'F0-4/H335', '经济学理论应用与教学', '经济学-教学研究', '本书分为上、下两篇,其主要内容包括:计量经济学理论应用与教学——FDI、对外贸易与经济增长及环境效应的实证研究;微观经济学、宏观经济学理论应用与教学——县域经济发展与经济学教学。', 2016, '贺文华', '978-7-5504-2801-0', '', 'F0-4', '', '西南财经大学出版社', '2019-01-04 17:58:16', '', NULL, '1208316', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('926', '1026', 'F0-49/X146', '经济学谬误:为反资本主义者所著', '经济学-通俗读物', '本书用丰富的事例和生动幽默的语言对资本主义经济学中左翼和右翼的12个错误观点，如资本主义制度是天然形成的、物质奖励刺激、税收过高、个人责任、价格公平、“疯狂地”追求利润、同工同酬、共享财富等进行了有力的批驳，作者见解深刻、阐述清晰，有效矫正了我们对于经济所持的错误观点。', 2016, '希思,余隽', '978-7-5155-0938-9', '', 'F0-49', '', '金城出版社', '2019-01-04 17:58:16', '', NULL, '1147047', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('927', '1027', 'F011/W183', '经济学能够成为硬科学吗?:方法论视角的研究', '经济学-方法论-研究', '本书内容包括：经济学的价值维度及其方法论含义；经济学的情境分析与复杂现象；经验检验与经济学的科学性的判断；经济学预测与大数据等。', 2016, '汪毅霖', '978-7-5096-4649-6', '', 'F011', '', '经济管理出版社', '2019-01-04 17:58:16', '', NULL, '1184172', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('928', '1028', 'F0/Y194a', '经济学实验案例教程', '经济学-高等学校-教材', '本书含括了十五个常用的经济学领域实验案例，旨在帮助经济学相关专业高年级本科生和研究生深入掌握常见经济模型及软件操作，同时希望对从事经济管理工作的政府机关、企事业单位从业人员具有一定的学习指导价值。', 2016, '杨飞虎', '978-7-5096-4001-2', '', 'F0-43', '', '经济管理出版社', '2019-01-04 17:58:16', '', NULL, '1147258', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('929', '1029', 'F0/H493', '经济学实证方法与价值中立原则研究', '经济学-研究', '本书从历史演变的角度，考察了不同的实证方法论在经济学的应用及其问题。全书共分六章，内容包括：经济学实证方法的演变、后危机时代主流经济学的实证性、实证经济学的价值中立原则等。', 2016, '胡明', '978-7-5096-4533-8', '', 'F0', '', '经济管理出版社', '2019-01-04 17:58:16', '', NULL, '1147257', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('93', '193', 'TP368.1/M1412', '单片机原理与应用系统设计', '单片微型计算机-高等学校-教材', '本书较全面和详细地介绍了单片机的基本原理、系统结构、指令系统、内部资源、外部接口扩展、应用系统设计实例以及嵌入式操作系统在单片机系统中应用等内容，并通过大量实例分析，重点讲解单片机应用系统的设计方法，以培养读者单片机应用系统设计能力。全书共12章。每章节的例题均由浅入深，循序渐进、讲解透彻。书中相关例程均在KeilC51', 2017, '马秀丽,周越,王红', '978-7-302-47559-0', '', 'TP368.1-43', '', '清华大学出版社', '2019-01-04 17:57:48', '', NULL, '1227021', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('930', '1030', 'null', '经济学是科学吗？:现代经济学的成效、历史与方法', '经济学-研究', '本书对二战以来经济学家为使本学科变为科学所作出的努力进行了历史回顾，探讨了科学与意识形态之间不断演变的关系，并对经济学中存在非正统学说和不同见解之处展开了调查研究。本书认为，尽管经济学面临各种问题，但还是需要这一学科去打破涉及经济问题的诸多神话。', 2018, '巴克豪斯,苏丽文', '978-7-5432-2816-0', '', 'F0', '', '格致出版社|上海人民出版社', '2019-01-04 17:58:16', '', NULL, '1264704', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('931', '1031', 'F129-53/Z284', '经济学视角下的中国大历史', '中国历史-经济学-文集,中国经济史-文集', '本书内容由高小勇教授主编,通过过经济学家深入浅出的讲解,以散文化的讲述,用经济学原理分析中国历史上的事件和人物,听经济学家梳理中国历史,从经济学的角度站在更新更广的视野看待中国历史,更新传统的历史观念。', 2017, '张五常,高小勇', '978-7-221-14026-5', '', 'F129-53,K207-53', '', '贵州人民出版社', '2019-01-04 17:58:16', '', NULL, '1205503', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('932', '1032', 'F0-49/L336c', '经济学思维:用经济学逻辑解决现实问题', '经济学-通俗读物', '本书分为三个部分,深入分析了改革开放以来,中国社会在变革中出现的一系列经济现象,并给出了相应的经济学解释。主要内容包括:改革开放的真正进步、栽花插柳——中国的未来之路等。', 2016, '李子旸', '978-7-5057-3872-0', '', 'F0-49', '', '中国友谊出版公司', '2019-01-04 17:58:16', '', NULL, '1170987', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('933', '1033', 'F0/P2422', '经济学通论', '经济学', '本书是在遵循两大理论体系的独立性基础上完成的,其体系结构包括上下两篇,即上篇为马克思主义政治经济学的基础知识,下篇为西方经济学的基础知识。我们认为,这样的体系结构安排不仅与当前两大理论体系在我国并存的现实相符合,也体现了我国经济建设中的主流意识。', 2017, '彭新万,杨勇,陶然,万春', '978-7-5096-5052-3', '', 'F0', '', '经济管理出版社', '2019-01-04 17:58:16', '', NULL, '1208203', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('934', '1034', 'F0/J121', '经济学通识课', '经济学-基本知识', '本书以时间为序分列章节，每章围绕一个经济史上的重要理论或者重要事件展开，记述了历史上重要的经济学家们的卓越贡献，如亚当•斯密、里卡多、马克思、凯恩斯等人，也分析了经济史上的重要话题，从货币的产生和平均地权论的兴起，到经济大萧条、企业家精神、环境破坏分配不均和行为经济学等问题。', 2017, '基什特尼,张缘,刘婧', '978-7-5139-1642-4', '', 'F0', '', '民主与建设出版社', '2019-01-04 17:58:16', '', NULL, '1228414', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('935', '1035', 'F0/L591', '经济学学科导论', '经济学', '本书内容：第一章至第六章，分别简要介绍了经济学在中国和西方的萌芽第七章至第九章，分别讨论了经济学的定义、逻辑起点、价值或财富的性质与源泉、微观主体经济行为规律、财富分配、市场失灵与囚徒困境、失业价飙与财富的生产、经济周期、国际分工贸易与金融问题。', 2016, '刘明国', '978-7-03-048598-4', '', 'F0', '', '科学出版社', '2019-01-04 17:58:16', '', NULL, '1143295', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('936', '1036', 'F0-52/L316a', '经济学研究:李翀文集', '经济学-文集', '本书为李翀教授的自选文集。全书分五个部分：政治经济学研究，西方经济学研究，世界经济研究，金融学研究，中国经济研究。本书是作者多年学术积淀的总结和展现，书中对中国经济现象的研究和对中国经济问题解决的思路，引人深思，给人启迪，具有可读性和现实意义。', 2016, '李翀', '978-7-5141-6325-4', '', 'F0-52', '', '经济科学出版社', '2019-01-04 17:58:16', '', NULL, '1151083', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('937', '1037', 'null', '经济学研究入门指南', '经济学-研究', '本书从经济学研究过程的角度比较系统的介绍了经济学经验研究的过程：题目的选择、文献的搜集和阅读、理论模型的构建、一手与二手数据的搜集与管理、模型的检验、论文写作及结果的展示。每一个内容都提供了大量的课外阅读文献和一些练习，是一本不可多得经济学经验研究的入门好书。', 2018, '格林劳,郝永敬', '978-7-301-29088-0', '', 'F0', '', '北京大学出版社', '2019-01-04 17:58:16', '', NULL, '1252883', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('938', '1038', 'F014.5/D1992', '经济学与消费者行为', '消费经济学-研究', '本书着重讨论该理论在福利经济学与计量分析中的应用。本书的前四章介绍了计量经济学的基本理论及其应用。这些章还包含该理论到劳动力供给、耐用商品、消费函数、配给等方面的初步扩展。本书的其余章节分为三个部分。在其中的第一部分，作者讨论了在选择和加总问题上的限制。第二部分的章节讨论了消费者指数、家庭特征、需求和福利比较，�', 2015, '德亚顿,米尔鲍尔,龚志民,宋旺,解烜', '978-7-300-22127-4', '', 'F014.5', '', '中国人民大学出版社', '2019-01-04 17:58:16', '', NULL, '1150312', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('939', '1039', 'F0/S836', '经济学原理', '经济学-高等学校-教材', '本书分为两部分，共15章。第一部分主要为主流教材中重点介绍的西方经济学部分的基本内容，主要包括供求理论、效用理论、生产理论、成本理论、市场理论、国民收入核算理论与方法、失业与通货膨胀、经济增长与经济周期、宏观经济政策；第二部分主要介绍经济学一级学科辖下的其他经济学知识，主要包括国际经济学常识、金融学常识、发展经济学', 2017, '孙倩', '978-7-5198-0195-3', '', 'F0-43', '', '中国电力出版社', '2019-01-04 17:58:16', '', NULL, '1187679', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
