mapper.select:
select s.id,s.read_user_id,s.title,s.content,s.gen_have,s.shape,s.status,s.reason,s.end_time,s.create_time,u.name as readUserName,u.phone as phone,u.email as email,u.work_adress as workAddress,u.adress as address from sys_gen s left join sys_read_user u on s.read_user_id=u.id

    and s.id = #{id}
    and s.read_user_id = #{readUserId}
    and s.title = #{title}
    and s.content = #{content}
    and s.gen_have = #{genHave}
    and s.shape = #{shape}
    and s.status = #{status}
    and s.reason = #{reason}
    and s.end_time = #{endTime}
    and s.create_time = #{createTime}



        order by s.${pager.sort} ${pager.order}


        order by s.id desc



    limit #{pager.pageIndex}, #{pager.pageSize}

select count(s.id) from sys_gen s left join sys_read_user u on s.read_user_id=u.id

    and s.id = #{id}
    and s.read_user_id = #{readUserId}
    and s.title = #{title}
    and s.content = #{content}
    and s.gen_have = #{genHave}
    and s.shape = #{shape}
    and s.status = #{status}
    and s.reason = #{reason}
    and s.end_time = #{endTime}
    and s.create_time = #{createTime}

mapper.update:
update sys_gen
read_user_id = #{readUserId}, title = #{title}, content = #{content}, gen_have = #{genHave}, shape = #{shape}, status = #{status}, reason = #{reason}, end_time = #{endTime}, create_time = #{createTime}
where id = #{id}
